mapper.insert:

  SELECT LAST_INSERT_ID()

insert into comment (userId, stroyId, content
  )
values (#{userid,jdbcType=INTEGER}, #{stroyid,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}
  )

mapper.update:
update comment
set userId = #{userid,jdbcType=INTEGER},
  stroyId = #{stroyid,jdbcType=INTEGER},
  content = #{content,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, userId, stroyId, content
from comment
where id = #{id,jdbcType=INTEGER}
